import React, { useState, useEffect } from 'react';
import { useAuth } from '../context/AuthContext';
import { interacoesAPI } from '../services/api';

const Carrinho = () => {
  const { isAuthenticated } = useAuth();
  const [itensCarrinho, setItensCarrinho] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    if (isAuthenticated) {
      carregarCarrinho();
    }
  }, [isAuthenticated]);

  const carregarCarrinho = async () => {
    try {
      const response = await interacoesAPI.minhas();
      const carrinho = response.data.filter(item => item.tipo === 'carrinho');
      setItensCarrinho(carrinho);
    } catch (error) {
      console.error('Erro ao carregar carrinho:', error);
    } finally {
      setLoading(false);
    }
  };

  const calcularTotal = () => {
    return itensCarrinho.reduce((total, item) => {
      return total + (item.produto_preco || 0);
    }, 0).toFixed(2);
  };

  if (!isAuthenticated) {
    return (
      <div className="container">
        <p>VocÃª precisa estar logado para ver seu carrinho.</p>
      </div>
    );
  }

  if (loading) {
    return <div className="container">Carregando...</div>;
  }

  return (
    <div className="container">
      <h1>Meu Carrinho</h1>

      {itensCarrinho.length > 0 ? (
        <>
          <div className="carrinho-lista">
            {itensCarrinho.map(item => (
              <div key={item.id} className="card carrinho-item">
                <div className="item-info">
                  <img 
                    src={item.produto_imagem || '/placeholder-tenis.jpg'} 
                    alt={item.produto_nome}
                    className="produto-thumb"
                  />
                  <div className="item-detalhes">
                    <h3>{item.produto_nome}</h3>
                    <p className="preco">R$ {item.produto_preco}</p>
                    {item.conteudo && (
                      <p className="observacoes">Obs: {item.conteudo}</p>
                    )}
                    <small>Adicionado em: {new Date(item.created_at).toLocaleDateString()}</small>
                  </div>
                </div>
              </div>
            ))}
          </div>

          <div className="carrinho-total">
            <div className="total-card">
              <h3>Total: R$ {calcularTotal()}</h3>
              <button className="btn btn-primary btn-finalizar">
                Finalizar Compra
              </button>
            </div>
          </div>
        </>
      ) : (
        <div className="carrinho-vazio">
          <p>Seu carrinho estÃ¡ vazio.</p>
          <a href="/produtos" className="btn btn-primary">Ver Produtos</a>
        </div>
      )}
    </div>
  );
};

export default Carrinho;
